iDer = 1;
while (iDer<=iD);
         sD = Dimnm('}Cubes', iDer);
         if (subst(sD,1,1)@<>'}');
                  myfoldername = Trim(sD) | '}vues';
                  if (FileExists(myfoldername)>0);
                           asciioutput(pOutputSite|'paDocc.bat', 'dir /B "' | pRUX | TRim(myfoldername) | '"  > "' | pOutputSite| Trim(sD) | 'views.txt"');
                  endif;
         endif;
         iDer = iDer + 1;
end;
                  
# --- application folders
myfoldername = '}Applications';
asciioutput(pOutputSite|'paDoca.bat', 'dir /B "' | pRUX | TRim(myfoldername) | '" > "' | pOutputSite| 'applicationfolders.txt');

# --- cube size
asciioutput(pOutputSite|'paDocs.bat', '(for /R  %%A in (*.cub) do echo %%~fA %%~zA) | findstr /v "echo" >"' | pOutputSite | 'assessedcubs.txt"');

# --- determine all current views for each cube for each user
iD = dimsiz('}Clients');
iC = dimsiz('}Cubes');
iCC = 1;
iDer = 1;
while (iDer<=iD);
         sD = Dimnm('}Clients', iDer);
         if (subst(sD,1,1)@<>'}');
                  myfoldername = Trim(sD);
                  if (FileExists(myfoldername)>0);
                           DimensionElementInsert('}sys_paDoc', '', Trim(sD), 'S');
                           while (iCC<=iC);
                                    sCube = Dimnm('}Cubes', iCC);
                                    if (FileExists(myfoldername | '/' | Trim(sCube) | '}vues')>0);
                                    asciioutput(pOutputSite|'paDocpri.bat', 'dir /B "' | pRUX | TRim(myfoldername) | '/' | Trim(sCube) | '}vues"  > "' | pOutputSite | Trim(sD) | '-' | Trim(sCube) | 'views.txt"');
                                    endif;
                                    iCC = iCC + 1;
                            end;
                           iCC = 1;
                  endif;
         endif;
         iDer = iDer + 1;
end;

